-- create table for program broadcasts
CREATE TABLE IF NOT EXISTS "PROGRAM_BROADCASTS" ("_id" INTEGER, "PROGRAM_ID" TEXT NOT NULL, "AIR_DATE" TEXT , "AIR_TIME" TEXT, "BROADCAST_TIMESTAMP" INTEGER UNIQUE, "LANGUAGE_ID" INTEGER NOT NULL, PRIMARY KEY ("_id", "PROGRAM_ID", "BROADCAST_TIMESTAMP"), FOREIGN KEY("PROGRAM_ID") REFERENCES PROGRAMS("PROGRAM_ID") ON DELETE CASCADE);